mapper.insert:
insert into comment(cm_num, cm_content, cm_me_id, cm_po_num, cm_ori_num) select ifnull(max(cm_num) + 1, 1), #{cm.cm_content}, #{cm.cm_me_id}, #{cm.cm_po_num},
ifnull(max(cm_num) + 1, 1) #{cm.cm_ori_num}
from comment

mapper.select:
select * from comment where cm_po_num = #{cri.search} 
order by cm_ori_num desc, cm_num asc
limit #{cri.pageStart}, #{cri.perPageNum};

mapper.delete:
delete from comment where

	cm_ori_num = #{cm.cm_num};


	cm_num = #{cm.cm_num};
